// Bài 1 : Tổng tiền lương

function tinhTongLuong(){
    var luong1Ngay = document.getElementById("luong1Ngay").value;
    var soNgay = document.getElementById("soNgay").value;

    console.log(luong1Ngay,soNgay); 
    var tongTien = luong1Ngay * soNgay;
    document.getElementById("txtSalary").innerHTML = tongTien;
   
 }

 document.getElementById(btnTongLuong).onclick = tongTien;

// bài 2 

function tinhTrungBinh() {
    var num1 = document.getElementById("number1").value;
    var num2 = document.getElementById("number2").value;
    var num3 = document.getElementById("number3").value;
    var num4 = document.getElementById("number4").value;
    var num5 = document.getElementById("number5").value;

    console.log(typeof(num1));

    var trungBinh = ( Number(num1) + Number(num2) + Number(num3) + Number(num4) + Number(num5) ) / 5; 

    document.getElementById("result").innerHTML = trungBinh; 
}

document.getElementById("btnTrungBinh").onclick = tinhTrungBinh;